#include"World.h"
World::World()
{
    tile_grid=vector<vector<Tile>>(WORLD_HEIGHT_TILES,vector<Tile>(WORLD_WIDTH_TILES));
    for(int i=0;i<WORLD_HEIGHT_TILES;i++)
        for(int j=0;j<WORLD_WIDTH_TILES;j++)
            {
                tile_grid[i][j].AssignPosition(i,j);
            }
}

void  World::DrawWorld(Vector2 playerposition)
{
    int playertilej=(int)(playerposition.x / TILE_SIZE) ;
    int playertilei=(int)(playerposition.y / TILE_SIZE) ;

    int lower_limit = min(playertilei + SCREEN_HEIGHT_TILES+6, WORLD_HEIGHT_TILES);
    int upper_limit = max(playertilei - SCREEN_HEIGHT_TILES-6, 0);
    int right_limit = min(playertilej + SCREEN_WIDTH_TILES+6, WORLD_WIDTH_TILES);
    int left_limit = max(playertilej - SCREEN_WIDTH_TILES-6, 0);
    //int lower_limit =WORLD_HEIGHT_TILES;
    //int upper_limit =0;
    //int right_limit =WORLD_WIDTH_TILES;
    //int left_limit =0;

    for(int i=upper_limit;i<lower_limit;i++)
        for(int j=left_limit;j<right_limit;j++)
            {
                if(tile_grid[i][j].texture_ID !=0)
                {
                    DrawTexturePro(TILE_MAPPER[tile_grid[i][j].texture_ID], 
                       { 0, 0, 70, 70 },    // Source rectangle from the tileset
                       tile_grid[i][j].dest,              // Destination rectangle
                       { 0, 0 },                          // Origin
                       0.0f,                              // Rotation
                       WHITE); 
                    /*DrawRectanglePro(                       
                        tile_grid[i][j].Hitbox,            
                       { 0, 0 },                          
                       0.0f,                              
                       BLUE);*/ 
                }
            }

}
